import { Component, OnInit } from '@angular/core';
import { BinarySearchTree } from '../../binary-search-tree';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent implements OnInit{
  bst: BinarySearchTree;
  numberToAdd: number;
  min: number | null;
  max: number | null;

  constructor() {
    this.bst = new BinarySearchTree();
    this.numberToAdd = 0;
    this.min = null;
    this.max = null;
  }

  ngOnInit(): void {
  }

  addToTree() {
    this.bst.insert(this.numberToAdd);
    this.min = this.bst.getMin();
    this.max = this.bst.getMax();
    this.numberToAdd = 0;
  }
}
